import React from 'react';
import ReactDOM from 'react-dom';
import { useState } from 'react';
import $ from 'jquery';
import './style.css';

function App() {
  return (
    <>
      <StatusBar />
      <div className='container'>
        <Frame type='river' />
        <Frame type='bank' />
      </div>
    </>
  )
}

function StatusBar() {
  return (
    <div className='statusBar'>
      <input className='searchBar'></input>
      <div className='buttonBar'>
        <button className='button' function='newTask()'>+</button>
        <button className='button' function='deleteTask()'>x</button>
        <button className='button' id='editTask()'>o</button>
        <button className='button' id='saveTask()'>|</button>
      </div>
    </div>
  )
}

function Frame({type}) {
  const [lists, setLists] = useState([
    { title: '1', text: '1'},
    { title: '2', text: '1'},
    { title: '3', text: '1'},
    { title: '4', text: '1'},
    { title: '5', text: '1'},
    { title: '6', text: '1'},
    { title: '7', text: '1'},
  ]);
  const div = (
    <div id={type} className='frame'>
      {lists.map(x => {
        return <List key={x.title} title={x.title} text={x.text} />
      })}
    </div>
  )
  return div;
}

function List({title, text}) {
  return (
    <div className='list'>
      <h1>{title}</h1>
      <ul>
        <Task />
      </ul>
    </div>
  )
}

function Task({info: {type}, text}) {
  return <li className='task'>task</li>
}

function newTask() {
  // create new task after selected
}

function deleteTask() {
  // delete selected task

}

function editTask() {
  // edit selected task
}

function saveTask() {
  // save selected task
}

function selectTask(task) {
  // make this task selected
}

function onClick(ev) {
  // handles clicks
  switch (ev.target.tagName) {
    case 'BUTTON':
      eval($(ev.target).attr('function'));
      break;
    case 'LI':
      break;
    case 'H1':
      break;
    default:
      break;
  }
}

function init() {
  ReactDOM.render(<App />, document.getElementById('root'));
  $(document).on('click', onClick)
}

init();
