import React, { useRef } from 'react';
import ReactDOM from 'react-dom';
import $ from 'jquery';
import './style.css';
import { render } from '@testing-library/react';

let selected;
let preventSelect;

function App() {
  return (
    <>
      <StatusBar />
      <div className='container'>
        <Frame type='river' />
        <Frame type='bank' />
      </div>
    </>
  )
}

function StatusBar() {
  return (
    <div className='statusBar'>
      <input className='searchBar'></input>
      <div className='buttonBar'>
        <button className='button' onClick={newTask}>+</button>
      </div>
    </div>
  )
}

class Frame extends React.Component {
  constructor() {
    super();
    this.state = {lists: [
      { title: '1', subtasks: []},
      { title: '2', subtasks: []},
      { title: '3', subtasks: []},
      { title: '4', subtasks: []},
      { title: '5', subtasks: []},
      { title: '6', subtasks: []},
      { title: '7', subtasks: []},
    ]}
  }
  render() {
    const div = (
      <div id={this.props.type} className='frame'>
        {this.state.lists.map(x => {
          return <List key={x.title} title={x.title} subtasks={x.subtasks} />
        })}
      </div>
    )
    return div;
  }
}

class List extends React.Component {
  constructor(props) {
    super(props);
    this.taskList = React.createRef();
  }
  render() {
    function selectThis() {
      selectTask(this);
    }
    selectThis = selectThis.bind(this);
    return (
      <div className='list' onClick={selectThis}>
        <h1>{this.props.title}</h1>
        <TaskList ref={this.taskList} subtasks={this.props.subtasks} />
      </div>
    )
  }
}

class TaskList extends React.Component {
  addTask() {
    const today = new Date();
    const now = today.getTime();
    console.log(now);
    const newTask={
      initialKey: now,
      initialInfo: {complete: ''},
      initialText: '',
      initialSubtasks: [],
    }
    const subtasks = this.state.subtasks;
    subtasks.push(newTask);
    this.setState({subtasks: subtasks});
  }
  constructor(props) {
    super();
    this.addTask = this.addTask.bind(this);
    this.state = {subtasks: props.subtasks, addTask: this.addTask};
  }
  render() {
    return (
      <ul>
        {this.state.subtasks.map(x => {
          return (
            <Task 
              key={x.initialKey}
              info={x.initialInfo}
              text={x.initialText}
              subtasks={x.initialSubtasks}
              parent={this}
            />
          )}
        )}
      </ul>
    )
  }
}

class Task extends React.Component {
  constructor(props) {
    super();
    this.state = {info: props.info, text: props.text, 
      subtasks: props.subtasks, parent: props.parent, 
      key: props.key, displayOptions: 'hide'};
    console.log(this.state);
  }
  displayOptions(ev) {
    console.log(ev.target);
    if ($(ev.target).hasClass('options') || 
      $(ev.target).parent().hasClass('options')) {
      console.log('failed');
      return
    }
    if (this.state.displayOptions == 'hide') {
      this.setState({displayOptions: 'show'});
    } else {
      this.setState({displayOptions: 'hide'});
    }
  }
  changeText(ev) { this.setState({text: ev.target.value}); }
  changeStartDate(ev) { 
    this.setState(prevState => ({
      info: {...prevState.info, startDate: ev.target.value}})); 
  }
  changeEndDate(ev) { 
    this.setState(prevState => ({
      info: {...prevState.info, endDate: ev.target.value}})); 
    console.log(this.state.info);
  }
  toggleComplete() {
    let status = this.state.info.complete
    if (status == 'complete') { status = '' }
    else { status = 'complete' }
    this.setState(prevState => ({
      info: {...prevState.info, complete: status}})); 
    console.log(this.state.info);
  }
  toggleImportant() {
    let status = this.state.info.important
    if (status == 'important') { status = '' }
    else { status = 'important' }
    this.setState(prevState => ({
      info: {...prevState.info, important: status, maybe: ''}})); 
    console.log(this.state.info);
  }
  toggleMaybe() {
    let status = this.state.info.maybe
    if (status == 'maybe') { status = '' }
    else { status = 'maybe' }
    this.setState(prevState => ({
      info: {...prevState.info, maybe: status, important: ''}})); 
    console.log(this.state.info);
  }
  deleteThis() {
    console.log(this.state.parent.state.subtasks, this.state.key);
    const subtasks = this.state.parent.state.subtasks;
    const currentTask = subtasks.find(x => {return x.key == this.state.key});
    console.log(currentTask);
    // this.state.parent.setState({subtasks: subtasks});
  }
  render() {
    // fuck react
    this.displayOptions = this.displayOptions.bind(this);
    this.toggleComplete = this.toggleComplete.bind(this);
    this.toggleImportant = this.toggleImportant.bind(this);
    this.toggleMaybe = this.toggleMaybe.bind(this);
    this.deleteThis = this.deleteThis.bind(this);
    // task with info and subtasks
    return (
      <li 
      className={'task ' + this.state.info} 
      onClick={() => {selectTask(this)}}
      >
        <span className='info' onClick={(ev) => this.displayOptions(ev)}>
          <div className={'options ' + this.state.displayOptions}>
            <button 
              className={'button ' + this.state.info.complete}
              onClick={this.toggleComplete}>
                √
              </button>
            <button 
              className={'button ' + this.state.info.important}
              onClick={this.toggleImportant}>
                !
              </button>
            <button 
              className={'button ' + this.state.info.maybe}
              onClick={this.toggleMaybe}>
                ?
              </button>
            <button 
              className={'button'}
              onClick={this.deleteThis}>
                x
              </button>
            <input className='optionsInput' 
              value={this.state.info.startDate} 
              onChange={(ev) => this.changeStartDate(ev)}>
            </input>
            <input className='optionsInput' 
              value={this.state.info.endDate} 
              onChange={(ev) => this.changeEndDate(ev)}>
            </input>
          </div>
        </span>
        <textarea className='editBar' value={this.state.text}
          onChange={(ev) => this.changeText(ev)}></textarea>
        <ul>
          <TaskList subtasks={this.state.subtasks} />
        </ul>
      </li>
    )
  }
}

function newTask(type) {
  // create new task after selected
  let el;
  if (!selected.state) {
    el = selected;
  } else if (selected.state.parent) {
    el = selected.state.parent;
  }
  console.log(el.taskList);
  el.taskList.current.state.addTask();
}

function selectTask(el) {
  // make this task selected
  if (preventSelect) return
  selected = el;
  preventSelect = true;
  setTimeout(function () { preventSelect = false }, 500)
  console.log(selected);
}

function init() {
  selected = undefined;
  ReactDOM.render(<App />, document.getElementById('root'));
}

init();
