import React, { useRef } from 'react';
import ReactDOM from 'react-dom';
import $ from 'jquery';
import './style.css';
import { render } from '@testing-library/react';

let selected;
let preventSelect;

function App() {
  return (
    <>
      <StatusBar />
      <div className='container'>
        <Frame type='river' />
        <Frame type='bank' />
      </div>
    </>
  )
}

function StatusBar() {
  return (
    <div className='statusBar'>
      <input className='searchBar'></input>
      <div className='buttonBar'>
        <button className='button' onClick={newTask}>+</button>
        <button className='button' onClick={deleteTask}>x</button>
      </div>
    </div>
  )
}

class Frame extends React.Component {
  constructor() {
    super();
    this.state = {lists: [
      { title: '1', subtasks: []},
      { title: '2', subtasks: []},
      { title: '3', subtasks: []},
      { title: '4', subtasks: []},
      { title: '5', subtasks: []},
      { title: '6', subtasks: []},
      { title: '7', subtasks: []},
    ]}
  }
  render() {
    const div = (
      <div id={this.props.type} className='frame'>
        {this.state.lists.map(x => {
          return <List key={x.title} title={x.title} subtasks={x.subtasks} />
        })}
      </div>
    )
    return div;
  }
}

class List extends React.Component {
  constructor(props) {
    super();
    this.addTask = this.addTask.bind(this);
    this.state = {subtasks: props.subtasks, addTask: this.addTask};
  }
  addTask(type) {
    const today = new Date();
    const now = today.getTime();
    if (!type) type = ''
    const newTask={
      key: now,
      initialInfo: '',
      initialText: '',
      initialSubtasks: [],
    }
    const subtasks = this.state.subtasks;
    subtasks.push(newTask);
    this.setState({subtasks: subtasks});
  }
  render() {
    function selectThis() {
      selectTask(this);
    }
    selectThis = selectThis.bind(this);
    return (
      <div className='list' onClick={selectThis}>
        <h1>{this.props.title}</h1>
        <TaskList subtasks={this.state.subtasks} />
      </div>
    )
  }
}

class TaskList extends React.Component {
  constructor(props) {
    super();
    this.state = {subtasks: props.subtasks};
  }
  render() {
    return (
      <ul>
        {this.state.subtasks.map(x => {
          return (
            <Task 
              key={x.key}
              info={x.initialInfo}
              text={x.initialText}
              subtasks={x.initialSubtasks}
              parent={this}
            />
          )}
        )}
      </ul>
    )
  }
}

class Task extends React.Component {
  constructor(props) {
    super();
    this.state = {info: props.info, text: props.text, 
      subtasks: props.subtasks, parent: props.parent, displayOptions: 'hide'};
    console.log(this.state);
  }
  displayOptions() {
    if (this.state.showOptions == 'hide') {
      this.setState({showOptions: 'show'});
      console.log('show');
    } else {
      this.setState({showOptions: 'hide'});
      console.log('hide');
    }
  }
  render() {
    function handleChange(e) {
      this.setState({text: e.target.value});
    }
    handleChange = handleChange.bind(this);
    this.displayOptions = this.displayOptions.bind(this);
    this.options = <div className={'options ' + this.state.displayOptions}></div>
    // task with info and subtasks
    return (
      <li 
      className={'task ' + this.state.info} 
      onClick={() => {selectTask(this)}}
      >
        {this.options}
        <span className='info' onClick={this.displayOptions}></span>
        <textarea className='editBar' value={this.state.text}
          onChange={handleChange}></textarea>
        <ul>
          <TaskList subtasks={this.state.subtasks} />
        </ul>
      </li>
    )
  }
}

function newTask(type) {
  // create new task after selected
  console.log(selected);
  let el;
  if (!selected.state.parent) {
    el = selected;
  } else if (selected.state.parent) {
    el = selected.state.parent;
  }
  el.state.addTask()
}

function deleteTask() {
  // edit selected task
}

function selectTask(el) {
  // make this task selected
  if (preventSelect) return
  selected = el;
  preventSelect = true;
  setTimeout(function () { preventSelect = false }, 500)
  console.log(selected);
}

function init() {
  selected = undefined;
  ReactDOM.render(<App />, document.getElementById('root'));
}

init();
