import React, { useRef } from 'react';
import ReactDOM from 'react-dom';
import $ from 'jquery';
import './style.css';
import { render } from '@testing-library/react';

let selected;

function App() {
  return (
    <>
      <StatusBar />
      <div className='container'>
        <Frame type='river' />
        <Frame type='bank' />
      </div>
    </>
  )
}

function StatusBar() {
  return (
    <div className='statusBar'>
      <input className='searchBar'></input>
      <div className='buttonBar'>
        <button className='button' onClick={newTask}>+</button>
        <button className='button' onClick={deleteTask}>x</button>
        <button className='button' onClick={editTask}>o</button>
        <button className='button' onClick={saveTask}>|</button>
      </div>
    </div>
  )
}

class Frame extends React.Component {
  constructor() {
    super();
    this.state = {lists: [
      { title: '1', subtasks: []},
      { title: '2', subtasks: []},
      { title: '3', subtasks: []},
      { title: '4', subtasks: []},
      { title: '5', subtasks: []},
      { title: '6', subtasks: []},
      { title: '7', subtasks: []},
    ]}
  }
  render() {
    const div = (
      <div id={this.props.type} className='frame'>
        {this.state.lists.map(x => {
          return <List key={x.title} title={x.title} subtasks={x.subtasks} />
        })}
      </div>
    )
    return div;
  }
}

class List extends React.Component {
  constructor(props) {
    super();
    this.state = {subtasks: props.subtasks, addTask: this.addTask};
    this.addTask = this.addTask.bind(this);
  }
  addTask(type) {
    const today = new Date();
    const now = today.getTime();
    if (!type) type = ''
    const newTask=(
      <Task 
        key={now}
        info={type}
        text=''
        subtasks={[]} 
        parent={this}
      />
    )
    console.log(this.state);
    const subtasks = this.state.subtasks;
    this.setState({subtasks: subtasks});
  }
  render() {
    function selectThis() {
      selectTask(this);
    }
    selectThis = selectThis.bind(this);
    return (
      <div className='list' onClick={selectThis}>
        <h1>{this.props.title}</h1>
        <ul>
          {this.state.subtasks.map(x => <Task 
            key={x.key}
            info={x.initialInfo}
            text={x.initialText}
            subtasks={x.initialSubtasks}
            parent={this}
          />)}
        </ul>
      </div>
    )
  }
}

class Task extends React.Component {
  constructor(props) {
    super();
    this.state = {info: props.info, text: props.text, 
      subtasks: props.subtasks, parent: props.parent};
  }
  render() {
    function handleChange(e) {
      this.setState({text: e.target.value});
    }
    handleChange = handleChange.bind(this);
    // task with info and subtasks
    return (
      <li 
      className={'task ' + this.state.info} 
      onClick={() => {selectTask(this)}}
      >
        <span className='info'></span>
        <textarea className='editBar' value={this.state.text}
          onChange={handleChange}></textarea>
        <ul>
          {this.state.subtasks.map(x => {
          return (
            <Task 
              key={x.key}
              info={x.initialInfo}
              text={x.initialText}
              subtasks={x.initialSubtasks}
              parent={this}
            />
          )})}
        </ul>
      </li>
    )
  }
}

function newTask(type) {
  // create new task after selected
  console.log(selected);
  let el;
  if (!selected.state.parent) {
    el = selected;
  } else if (selected.state.parent) {
    el = selected.state.parent;
  }
  el.state.addTask()
}

function deleteTask() {
  // delete selected task

}

function editTask() {
  // edit selected task
}

function saveTask() {
  // save selected task
}

function selectTask(el) {
  // make this task selected
  selected = el;
  console.log(selected);
}

function init() {
  selected = undefined;
  ReactDOM.render(<App />, document.getElementById('root'));
}

init();
