import React from 'react';
import ReactDOM from 'react-dom';
import { useState } from 'react';
import $ from 'jquery';
import './style.css';

let selected;

function App() {
  return (
    <>
      <StatusBar />
      <div className='container'>
        <Frame type='river' />
        <Frame type='bank' />
      </div>
    </>
  )
}

function StatusBar() {
  return (
    <div className='statusBar'>
      <input className='searchBar'></input>
      <div className='buttonBar'>
        <button className='button' onClick={newTask}>+</button>
        <button className='button' onClick={deleteTask}>x</button>
        <button className='button' onClick={editTask}>o</button>
        <button className='button' onClick={saveTask}>|</button>
      </div>
    </div>
  )
}

function Frame({type}) {
  const [lists, setLists] = useState([
    { title: '1', text: '1'},
    { title: '2', text: '1'},
    { title: '3', text: '1'},
    { title: '4', text: '1'},
    { title: '5', text: '1'},
    { title: '6', text: '1'},
    { title: '7', text: '1'},
  ]);
  const div = (
    <div id={type} className='frame'>
      {lists.map(x => {
        return <List key={x.title} title={x.title} text={x.text} />
      })}
    </div>
  )
  return div;
}

function List({title, text}) {
  return (
    <div className='list'>
      <h1>{title}</h1>
      <ul>
        <Task initialInfo='' initialText='' initialSubtasks={[]} 
          initialParent={this} />
      </ul>
    </div>
  )
}

function Task({initialInfo, initialText, initialSubtasks, initialParent}) {
  // task with info and subtasks
  const [subtasks, setSubtasks] = useState(initialSubtasks);
  const [text, setText] = useState(initialText);
  const [parent, setParent] = useState(initialParent);
  const handleChange = (event) => { setText(event.target.value) };
  const inputElement = useRef();
  return (
    <li 
      className={'task ' + initialInfo} 
      onClick={(event) => { selectTask(event.target) }}
      ref={inputElement}
    >
      <span className='info'></span>
      <textarea className='editBar' value={text}
        onChange={handleChange}></textarea>
      <ul>
        {initialSubtasks.map(x => <Task 
          initialInfo={x.initialInfo} 
          initialText={x.initialText} 
          initialSubtasks={x.initialSubtasks}
          initialParent={this}
        />)}
      </ul>
    </li>
  )
}

function newTask() {
  // create new task after selected
  console.log(selected, );
}

function deleteTask() {
  // delete selected task

}

function editTask() {
  // edit selected task
}

function saveTask() {
  // save selected task
}

function selectTask(el) {
  // make this task selected
  selected = el;
}

function init() {
  selected = undefined;
  ReactDOM.render(<App />, document.getElementById('root'));
}

init();
