import React, { useRef } from 'react';
import ReactDOM from 'react-dom';
import $ from 'jquery';
import './style.css';
import { render } from '@testing-library/react';

let selected;

function App() {
  return (
    <>
      <StatusBar />
      <div className='container'>
        <Frame type='river' />
        <Frame type='bank' />
      </div>
    </>
  )
}

function StatusBar() {
  return (
    <div className='statusBar'>
      <input className='searchBar'></input>
      <div className='buttonBar'>
        <button className='button' onClick={newTask}>+</button>
        <button className='button' onClick={deleteTask}>x</button>
        <button className='button' onClick={editTask}>o</button>
        <button className='button' onClick={saveTask}>|</button>
      </div>
    </div>
  )
}

class Frame extends React.Component {
  render() {
    this.state = {lists: [
      { title: '1', text: '1'},
      { title: '2', text: '1'},
      { title: '3', text: '1'},
      { title: '4', text: '1'},
      { title: '5', text: '1'},
      { title: '6', text: '1'},
      { title: '7', text: '1'},
    ]}
    const div = (
      <div id={this.props.type} className='frame'>
        {this.state.lists.map(x => {
          return <List key={x.title} title={x.title} text={x.text} />
        })}
      </div>
    )
    return div;
  }
}

class List extends React.Component {
  render() {
    return (
      <div className='list'>
        <h1>{this.props.title}</h1>
        <ul>
          <Task info='' text='' subtasks={[]} />
        </ul>
      </div>
    )
  }
}

class Task extends React.Component {
  constructor(props) {
    super();
    this.state = {info: props.info, text: props.text, 
      subtasks: props.subtasks, parent: props.parent};
  }
  render() {
    function handleChange(e) {
      console.log(this);
      this.setState({text: e.target.value});
    }
    // task with info and subtasks
    return (
      <li 
      className={'task ' + this.state.info} 
      onClick={() => {selectTask(this)}}
      >
        <span className='info'></span>
        <textarea className='editBar' value={this.state.text}
          onChange={handleChange}></textarea>
        <ul>
          {this.state.subtasks.map(x => <Task 
            info={x.initialInfo}
            text={x.initialText}
            subtasks={x.initialSubtasks}
            parent={this}
          />)}
        </ul>
      </li>
    )
  }
}

function newTask() {
  // create new task after selected
}

function deleteTask() {
  // delete selected task

}

function editTask() {
  // edit selected task
}

function saveTask() {
  // save selected task
}

function selectTask(el) {
  // make this task selected
  selected = el;
  console.log(selected);
}

function init() {
  selected = undefined;
  ReactDOM.render(<App />, document.getElementById('root'));
}

init();
